export default (state, action) => {
  switch(action.type) {
    case 'GET_INFO':
    return {
      ...state,
      bullepoints: action.payload
    }
    case 'DELETE_POINT':
      return {
        ...state,
        bullepoints: state.bullepoints.filter(bulletpoint => bulletpoint.id !== action.payload)
      }
    case 'ADD_POINT':
      return {
        ...state,
        bullepoints: [...state.bullepoints, action.payload]
      }
    default:
      return state;
  }
}
