import React, { createContext, useReducer } from 'react';
import AppReducer from './AppReducer';
// import axios from 'axios';

// Initial state
const initialState = {
  transactions: [
    {id: 1, type: 'qualification', text:'Operating systems: Linus[CentOS,Ubuntu], Windows [XP-10], MAC OSX.'},
    {id: 2, type: 'project_experience', text:'Use iterator pattern and information hiding principal to implement linkedlist, singleton pattern to access chess\
board and handle errors, command pattern to handle execute, redo and undo features.'},
    {id: 3, type: 'volunteer_experience', text:'Provide leadership, guidance and support to new members with event writing and article editing, public social media\
maintenance.'},
    {id: 4, type: 'interests', text:'Snowboarding, Programming, Soccer, Video Games'},
  ]

}

// Create context
export const GlobalContext = createContext(initialState);

// Provider component
export const GlobalState = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  // Actions

  function deleteTransaction(id) {
    dispatch({
      type: 'DELETE_TRANSACTION',
      payload: id
    });
  }

  function addTransaction(transaction) {

      dispatch({
        type: 'ADD_TRANSACTION',
        payload: transaction
      });
  }

  return (<GlobalContext.Provider value={{
    transactions: state.transactions,
    deleteTransaction,
    addTransaction
  }}>
    {children}
  </GlobalContext.Provider>);
}
