import React, {useState, useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';

export const AddTransaction = () => {
  const [text, setText] = useState('');
  const [type, setType] = useState('qualification');

  const { addBulletPoint } = useContext(GlobalContext);

  const onSubmit = e => {
    e.preventDefault();
    const newPoint = {
      id: Math.floor(Math.random() * 100000000),
      text,
      type
    }
    addBulletPoint(newPoint);
  }

  return (
    <>
      <h3>Add BulletPoint</h3>
      <form >
        <div className="form-control">
          <label htmlFor="text">Text</label>
          <input type="text" value={text} onChange={(e) => setText(e.target.value)} placeholder="Enter text..." />
        </div>
        <div className="form-control">
          <label htmlFor="type"
            >Type<br />
          </label>
          <select className="options" value={type} onChange={(e) => setType(e.target.value)}>
          <option value="qualification">qualification</option>
          <option value={"education"}>education</option>
          <option value={"projectExperience"}>projectExperience</option>
          <option value={"volunteerExperience"}>volunteerExperience</option>
          <option value={"interests"}>interests</option>
          </select>
        </div>
        <button className="btn" onClick = {onSubmit}>Add BulletPoint</button>
      </form>
    </>
  )
}
