import React, { createContext, useReducer } from 'react';
import AppReducer from './AppReducer';
// import axios from 'axios';

// Initial state
const initialState = {
  bullepoints: [
    {id: 1, font: 'normal', into: 'qualification', text:'Operating systems: Linus[CentOS,Ubuntu], Windows [XP-10], MAC OSX.'},
    {id: 5, font: 'normal', into: 'qualification', text:'Operating systems: Linus[CentOS,Ubuntu], Windows [XP-10], MAC OSX.'},
    {id: 2, font: 'normal', into: 'project_experience', text:'Use iterator p'},
    {id: 3, font: 'bold', into: 'volunteer_experience', text:'Provide leadership, guidance and support'},
    {id: 4, font: 'italic', into: 'interests', text:'Snowboarding, Programming, Soccer, Video Games'}  ]

}

// Create context
export const GlobalContext = createContext(initialState);

// Provider component
export const GlobalState = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  // Actions

  function deleteBulletPoint(id) {
    dispatch({
      type: 'DELETE_POINT',
      payload: id
    });
  }

  function addBulletPoint(point) {

      dispatch({
        type: 'ADD_POINT',
        payload: point
      });
  }

  return (<GlobalContext.Provider value={{
    bullepoints: state.bullepoints,
    deleteBulletPoint,
    addBulletPoint
  }}>
    {children}
  </GlobalContext.Provider>);
}
